import * as THREE from 'three'
import AudioController from '../../components/utils/AudioController';

export default class Sphere {
    constructor() {
        this.geometry = new THREE.SphereGeometry(15, 32, 16);
        this.material = new THREE.MeshNormalMaterial({ color: 0xffff00 });
        this.mesh = new THREE.Mesh(this.geometry, this.material);
        console.log(this.mesh)
    }
    tick() {
        this.mesh.rotation.x += 0.01;

        const remapped = AudioController.fdata[0] / 255;

        this.mesh.geometry.parameters.radius.set(
            1 + remapped,
            1 + remapped,
            1 + remapped)
    }
}
