import * as THREE from 'three'
import AudioController from '../../components/utils/AudioController';

export default class Sphere {
    constructor() {
        const particleCount = 1000;
        const sphereRadius = 5;

        this.geometry = new THREE.SphereBufferGeometry(sphereRadius, 32, 32);

        const vertices = new Float32Array([
            -1.0, -1.0, 1.0, // v0
            1.0, -1.0, 1.0, // v1
            1.0, 1.0, 1.0, // v2

            1.0, 1.0, 1.0, // v3
            -1.0, 1.0, 1.0, // v4
            -1.0, -1.0, 1.0  // v5
        ]);
        this.geometry.setAttribute('position', new THREE.BufferAttribute(vertices, 3));
        this.material = new THREE.MeshNormalMaterial({
            wireframe: true
        });
        this.mesh = new THREE.Mesh(this.geometry, this.material);
        console.log(this.mesh)
        this.group = new THREE.Group();
        this.group.add(this.mesh)
    }
    tick() {
        const remapped = AudioController.fdata[0] / 255;

        // this.mesh.geometry.verticesNeedUpdate.set(
        //     1 + remapped,
        //     1 + remapped,
        //     1 + remapped)
    }
}
