import * as THREE from "three";

class SCENE {
    setup(canvas){
        this.canvas=canvas,
        this.width=window.innerWidth,
        this.height=window.innerHeight
    }
}
const Scene = new SCENE();
// export d'une instance de la classe et non de la classe elle même
export default Scene
