import * as THREE from 'three';
import AudioController from '../../components/utils/AudioController';

export default class Sphere {
    constructor(frequenceMin, frequenceMax, extrusionAmount) {
        this.geometry = new THREE.SphereGeometry(15, 32, 16);
        this.material = new THREE.MeshNormalMaterial({ color: 0xffff00 });
        this.mesh = new THREE.Mesh(this.geometry, this.material);

        this.frequenceMin = frequenceMin;
        this.frequenceMax = frequenceMax;
        this.extrusionAmount = extrusionAmount;
    }

    tick() {
        this.mesh.rotation.x += 0.01;

        const frequency = AudioController.fdata[0] / 255;
        const remapped = this.map(frequency, this.frequenceMin, this.frequenceMax, 0, this.extrusionAmount);

        const vertices = this.mesh.geometry.vertices;

        for (let i = 0; i < vertices.length; i++) {
            const vertex = vertices[i];
            const direction = vertex.clone().normalize().multiplyScalar(remapped);
            vertex.add(direction);
        }

        this.mesh.geometry.verticesNeedUpdate = true;
    }

    map(value, inMin, inMax, outMin, outMax) {
        return (value - inMin) * (outMax - outMin) / (inMax - inMin) + outMin;
    }
}
