class AUDIO_CONTROLLER{
    setup(){
        this.audio = new Audio()
    }
    updateSong(preview){
        console.log(preview)

        this.audio.src = preview;
        this.audio.currentTime = 0;
        this.audio.play();
    }
}
const AudioController = new AUDIO_CONTROLLER()
export default AudioController
